import { IonButton, IonContent, IonHeader, IonInput, IonItem, IonLabel, IonPage, IonTitle, IonToolbar } from '@ionic/react'
import React, { useEffect, useRef, useState } from 'react'

const Profile: React.FC<any> = ({user, setuser}) => {
    const [imgFile, setimgFile] = useState<Blob>()


    function updateHandler() {
        let formData  = new FormData();
        formData.append('imgFile', imgFile);
        formData.append('name',user.name);
        fetch("http://192.168.43.228:5000/profileUpdate",{
            method:'POST',
            headers: {
             'Content-Type': "multipart/form-data"
            },
            body: formData
        }).then(res=>res.json())
        .then(res=> {
            setuser({...user,avatar: res.imgPath});
        });
    }
    return (
        <IonPage>
            <IonHeader>
                <IonToolbar>
                    <IonTitle>Profile</IonTitle>
                </IonToolbar>
            </IonHeader>
            <IonContent>
                <img src={`http://192.168.43.228:5000/${user.avatar}`} alt=""></img>
                <div>{user.name}</div>
                <IonItem>
                    <IonLabel position="stacked">Update Name</IonLabel>
                    <IonInput value={user.name}></IonInput>
                </IonItem>
                <IonItem>
                    <IonLabel position="stacked">Upload Image</IonLabel>
                    <input className="my-3" type="file" onChange={(e)=>setimgFile(e.target.files[0])} />
                </IonItem>
                <IonButton size="default" expand="block" onClick={updateHandler} color="primary">Update</IonButton>
            </IonContent>
        </IonPage>
    )
}

export default Profile
