import { IonButton, IonContent, IonHeader, IonInput, IonItem, IonLabel, IonPage, IonTitle, IonToast, IonToolbar } from '@ionic/react';
import { useState } from 'react';

const ForgotPassword: React.FC = () => {
  const [token, settoken] = useState("")
  const [email, setemail] = useState("")
  const [newpassword, setnewpassword] = useState("");
  const [confirmpassword, setconfirmpassword] = useState("");
  const [showToast, setshowToast] = useState(false);
  const [toastMessage, settoastmessage] = useState("");
  const [toastType, settoastType] = useState("");
  const [emailSent, setemailSent] = useState(false);


    function sendTokenHandler() {
      if (email == "") {
        settoastType("danger");
        settoastmessage("Email should not be empty");
        setshowToast(true);
        return;
      }
      let resStatus=0;
      fetch("http://localhost:5000/sendForgotPasswordToken", {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ email })
      }).then(res => { resStatus = res.status; return res.json() })
        .then(res => {
          if (resStatus == 200) {
            settoastType("success");
            settoastmessage(res.message);
            setshowToast(true);
            setemailSent(true);
          }
          else {
            settoastType("danger");
            settoastmessage(res.error);
            setshowToast(true);
          }
        });
    }

    function sendToken() {
      return (<div>
        <IonItem>
          <IonLabel position="stacked">Email</IonLabel>
          <IonInput value={email} onIonChange={(e) => setemail(e.detail.value)}></IonInput>
        </IonItem>
        <IonButton size="default" expand="block" onClick={sendTokenHandler} color="primary">Send Reset Token</IonButton>
      </div>
      )
    }



  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonTitle>Forgot Password</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent className="ion-padding-top">
      { sendToken() }
        <IonToast
          isOpen={showToast}
          onDidDismiss={() => setshowToast(false)}
          message={toastMessage}
          duration={2000}
          color={toastType}
        />
      </IonContent>

    </IonPage>
  );
};

export default ForgotPassword;
