import { IonButton, IonContent, IonHeader, IonInput, IonItem, IonLabel, IonPage, IonTitle, IonToast, IonToolbar } from '@ionic/react';
import { useState } from 'react';

const Register: React.FC = () => {
  const [name, setname] = useState("");
  const [email, setemail] = useState("")
  const [confirmpassword, setconfirmpassword] = useState("");
  const [password, setpassword] = useState("");
  const [showToast, setshowToast] = useState(false);
  const [toastMessage, settoastmessage] = useState("");

  const RegisterHandler = () => {
    let resStatus = 0;
    fetch("http://localhost:5000/login", {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ email, password })
    }).then(res => { resStatus = res.status; return res.json() })
      .then(res => {
        if (resStatus == 200) {
          settoastmessage(res.message);
          setshowToast(true);
          localStorage.setItem('token', res.token);
        }
        else {
          settoastmessage(res.error);
          setshowToast(true);
        }
      });
  }


  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonTitle>Register</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent className="ion-padding-top">
      <IonItem>
          <IonLabel position="stacked">Name</IonLabel>
          <IonInput value={name} onIonChange={(e) => setname(e.detail.value)}></IonInput>
        </IonItem>
        <IonItem>
          <IonLabel position="stacked">Email</IonLabel>
          <IonInput value={email} onIonChange={(e) => setemail(e.detail.value)}></IonInput>
        </IonItem>
        <IonItem>
          <IonLabel position="stacked">Password</IonLabel>
          <IonInput type="password" value={password} onIonChange={(e) => setpassword(e.detail.value)}></IonInput>
        </IonItem>
        <IonItem>
          <IonLabel position="stacked">Confirm Password</IonLabel>
          <IonInput type="password" value={confirmpassword} onIonChange={(e) => setconfirmpassword(e.detail.value)}></IonInput>
        </IonItem>
        <IonButton size="default" expand="block" onClick={RegisterHandler} color="primary">Register</IonButton>
        <IonToast
          isOpen={showToast}
          onDidDismiss={() => setshowToast(false)}
          message={toastMessage}
          duration={2000}
        />
      </IonContent>

    </IonPage>
  );
};

export default Register;
