import { IonBackButton, IonButton, IonButtons, IonContent, IonFab, IonFabButton, IonHeader, IonIcon, IonInput, IonItem, IonLabel, IonPage, IonTitle, IonToast, IonToolbar } from '@ionic/react'
import React, { useEffect, useRef, useState } from 'react'
import { Camera, CameraResultType } from '@capacitor/camera';
import { cameraOutline as cameraOutlineIcon, createOutline } from 'ionicons/icons';

const Profile: React.FC<any> = ({ user, setuser }) => {
    const [imgFile, setimgFile] = useState<Blob>();
    const [previewImgUrl, setpreviewImgUrl] = useState("");
    const [showToast, setshowToast] = useState(false);
    const [toastMessage, settoastmessage] = useState("");
    const [toastType, settoastType] = useState("");
    let inputref = useRef<any>();

    async function camerahandler() {
        let photo = await Camera.getPhoto({
            resultType: CameraResultType.Uri
        });
        let res = await fetch(photo.webPath);
        let imgFile = await res.blob();
        setimgFile(imgFile);
        setpreviewImgUrl(photo.webPath);
    }

    function updateHandler() {
        let formData = new FormData();
        if (!inputref.current.value) {
            settoastType("danger");
            settoastmessage("All fields are mandatory");
            setshowToast(true);
            return;
        }
        formData.append('imgFile', imgFile);
        formData.append('name', inputref.current.value);
        formData.append('userId', user._id);
        let resStatus = 0;
        fetch("http://192.168.43.228:5000/updateProfile", {
            method: 'POST',

            body: formData
        }).then(res => { resStatus = res.status; return res.json() })
            .then(res => {
                if (resStatus == 200) {
                    setuser(res.user);
                    setpreviewImgUrl("");
                    settoastType("success");
                    settoastmessage(res.message);
                    setshowToast(true);
                }
                else {
                    settoastType("danger");
                    settoastmessage(res.error);
                    setshowToast(true);
                }
            });
    }
    return (
        <IonPage>
            <IonHeader>
                <IonToolbar>
                    <IonButtons slot="start">
                        <IonBackButton></IonBackButton>
                    </IonButtons>
                    <IonTitle>Profile</IonTitle>
                </IonToolbar>
            </IonHeader>
            <IonContent>
                <div className="text-center mt-3">
                    <img src={`http://192.168.43.228:5000/${user.avatar}`} alt="" style={{ height: "200px", width: "200px", borderRadius: "50%" }}></img>
                    <div className="h4 mt-3 text-primary">{user.name}</div>
                </div>

                <IonItem>
                    <IonLabel position="stacked">Update Name</IonLabel>
                    <IonInput ref={inputref} value={user.name}></IonInput>
                </IonItem>
                <IonItem>
                    <IonLabel position="stacked">Upload Image</IonLabel>
                    <IonIcon icon={cameraOutlineIcon} onClick={camerahandler} />
                    {/* <IonButton onClick={camerahandler}>Take Picture</IonButton> */}
                    {/* <input className="my-3" type="file" onChange={(e) => setimgFile(e.target.files[0])} /> */}
                </IonItem>
                {previewImgUrl ? <div className="text-center">
                    <div className="mt-3">Preview Image
               </div>
                    <img src={previewImgUrl} style={{ height: "150px", width: "150px", borderRadius: "50%" }} alt="preview"></img>
                </div> : ""}
                <IonButton size="default" expand="block" onClick={updateHandler} color="primary">Update</IonButton>



                <IonFab vertical="bottom" horizontal="end" slot="fixed">
                    <IonFabButton>
                        <IonIcon icon={createOutline} />
                    </IonFabButton>
                </IonFab>
            </IonContent>
        </IonPage>
    )
}

export default Profile
