import { IonButton, IonContent, IonHeader, IonInput, IonItem, IonLabel, IonLoading, IonPage, IonSpinner, IonTitle, IonToast, IonToolbar } from '@ionic/react';
import { useState } from 'react';

const ForgotPassword: React.FC = () => {
  const [token, settoken] = useState("")
  const [email, setemail] = useState("")
  const [newpassword, setnewpassword] = useState("");
  const [confirmpassword, setconfirmpassword] = useState("");

  const [showToast, setshowToast] = useState(false);
  const [toastMessage, settoastmessage] = useState("");
  const [toastType, settoastType] = useState("");

  const [emailSending, setemailSending] = useState(false);
  const [emailSent, setemailSent] = useState(false);
  const [passwordChanging, setpasswordChanging] = useState(false);
  const [passwordChanged, setpasswordChanged] = useState(false);
  let component;
  function renderComponent() {

    if (!(emailSending && emailSent))
      component = sendToken();
    else if (emailSending) {
      component = spinner();
    }
    else if (passwordChanged)
      component = passwordChangedSuccessfully();
    else if (emailSent)
      component = resetpassword();

  }

  function sendTokenHandler() {
    if (email == "") {
      settoastType("danger");
      settoastmessage("Email should not be empty");
      setshowToast(true);
      return;
    }
    let resStatus = 0;
    setemailSending(true);
    fetch("http://localhost:5000/sendForgotPasswordToken", {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ email })
    }).then(res => { resStatus = res.status; return res.json() })
      .then(res => {
        if (resStatus == 200) {
          settoastType("success");
          settoastmessage(res.message);
          setshowToast(true);
          setemailSent(true);
          setemailSending(false);
        }
        else {
          settoastType("danger");
          settoastmessage(res.error);
          setshowToast(true);
          setemailSending(false);
        }
      });
  }

  function sendToken() {
    return (<div>
      <IonItem>
        <IonLabel position="stacked">Email</IonLabel>
        <IonInput value={email} onIonChange={(e) => setemail(e.detail.value)}></IonInput>
      </IonItem>
      <IonButton size="default" expand="block" onClick={sendTokenHandler} color="primary">Send Reset Token</IonButton>
    </div>
    )
  }

  function resetPasswordHandler() {
    if (token == "" || newpassword == "") {
      settoastType("danger");
      settoastmessage("All fields are mandatory");
      setshowToast(true);
      return;
    }
    if (newpassword !== confirmpassword) {
      settoastType("danger");
      settoastmessage("Passwords Should be Matched");
      setshowToast(true);
      return;
    }

    let resStatus = 0;
    setpasswordChanging(true);
    fetch("http://localhost:5000/verifyForgotPasswordDetails", {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ token, password: newpassword })
    }).then(res => { resStatus = res.status; return res.json() })
      .then(res => {
        if (resStatus == 200) {
          settoastType("success");
          settoastmessage(res.message);
          setshowToast(true);
          setpasswordChanging(false);
          setpasswordChanged(true);
        }
        else {
          settoastType("danger");
          settoastmessage(res.error);
          setshowToast(true);
          setpasswordChanging(false);
        }
      });
  }

  function passwordChangedSuccessfully() {
    return (
      <div>
        <div className="text-primary h3">
          Password Changed Successfully
        </div>
        <IonButton routerLink="/login" fill="clear">Go to Login Page</IonButton>
      </div>
    )
  }

  function resetpassword() {
    <div>
      <IonItem>
        <IonLabel position="stacked">Token</IonLabel>
        <IonInput value={token} onIonChange={(e) => settoken(e.detail.value)}></IonInput>
      </IonItem>
      <IonItem>
        <IonLabel position="stacked">New Password</IonLabel>
        <IonInput value={newpassword} onIonChange={(e) => setnewpassword(e.detail.value)}></IonInput>
      </IonItem>
      <IonItem>
        <IonLabel position="stacked">Confrim Password</IonLabel>
        <IonInput value={confirmpassword} onIonChange={(e) => setconfirmpassword(e.detail.value)}></IonInput>
      </IonItem>
      <IonButton size="default" expand="block" onClick={resetPasswordHandler} color="primary">Set Password</IonButton>
      <IonLoading isOpen={passwordChanging} />
    </div>
  }


  function spinner() {
    <IonSpinner name="crescent" />
  }



  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonTitle>Forgot Password</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent className="ion-padding-top">


        {component}s

        <IonToast
          isOpen={showToast}
          onDidDismiss={() => setshowToast(false)}
          message={toastMessage}
          duration={2000}
          color={toastType}
        />
      </IonContent>

    </IonPage>
  );
};

export default ForgotPassword;
