import { IonButton, IonContent, IonHeader, IonInput, IonItem, IonLabel, IonPage, IonTitle, IonToast, IonToolbar } from '@ionic/react'
import React, { useEffect, useRef, useState } from 'react'

const Profile: React.FC<any> = ({ user, setuser }) => {
    const [imgFile, setimgFile] = useState<Blob>()
    const [showToast, setshowToast] = useState(false);
    const [toastMessage, settoastmessage] = useState("");
    const [toastType, settoastType] = useState("");
    let inputref = useRef<any>();

    function updateHandler() {
        let formData = new FormData();
        console.log(inputref.current.value);
        formData.append('imgFile', imgFile);
        formData.append('name',user.name);
        formData.append('userId', user._id);
        let resStatus = 0;
        fetch("http://192.168.43.228:5000/updateProfile", {
            method: 'POST',

            body: formData
        }).then(res => { resStatus = res.status; return res.json() })
            .then(res => {
                if (resStatus == 200) {
                    setuser(res.user);
                    settoastType("success");
                    settoastmessage(res.message);
                    setshowToast(true);
                }
                else {
                    settoastType("danger");
                    settoastmessage(res.error);
                    setshowToast(true);
                }
            });
    }
    return (
        <IonPage>
            <IonHeader>
                <IonToolbar>
                    <IonTitle>Profile</IonTitle>
                </IonToolbar>
            </IonHeader>
            <IonContent>
                <div className="text-center mt-3">
                <img src={`http://192.168.43.228:5000/${user.avatar}`} alt=""  style={{height: "200px" , width: "200px", borderRadius:"50%"}}></img>
                <div className="h4 mt-3 text-primary">{user.name}</div>
                </div>

                <IonItem>
                    <IonLabel position="stacked">Update Name</IonLabel>
                    <IonInput ref={inputref} value={user.name}></IonInput>
                </IonItem>
                <IonItem>
                    <IonLabel position="stacked">Upload Image</IonLabel>
                    <input className="my-3" type="file" onChange={(e) => setimgFile(e.target.files[0])} />
                </IonItem>
                <IonButton size="default" expand="block" onClick={updateHandler} color="primary">Update</IonButton>
                <IonToast
                    isOpen={showToast}
                    onDidDismiss={() => setshowToast(false)}
                    message={toastMessage}
                    duration={2000}
                    color={toastType}
                />
            </IonContent>
        </IonPage>
    )
}

export default Profile
